   //This defines the Stock object:
    Stock.prototype.value=function(){return this.qty*this.buyPrice;};
    function Stock(index,ticker,qty,buyPrice,loLim,hiLim) 
    {
      this.Index = index;
      this.Ticker = ticker;
      this.Qty = qty;
      this.BuyPrice = buyPrice;
      this.HiLim = hiLim;
      this.LoLim = loLim;
      this.Last = 0;
      this.DayHigh = 0;
      this.DayLow = 0;
      this.DayRange = this.DayLow + " - " + this.DayHigh;
      this.Open = 0;
      this.Change = 0;
      this.ChangePct = 0;
      this.DayVolume = 0;
      this.Exchange = "";
      this.TradeDateTime = "";
      this.Updated = 0;
      if (this.Last>this.HiLim){OnHi(this.Index);}
      if (this.Last<this.LoLim){OnLo(this.Index);}
      if (this.Updated==1){UpdateStock(this.Index);}
    }

    function UpdateStock(i)
    {
      msg('Stock "' + Stocks[i].Ticker + '" was updated');
    }
    
    function OnHi(i)
    {
      //msg('Hi limit passed on index: '+i);
    }
    
    function OnLo(i)
    {
      //msg('Lo limit passed on index: '+i);
    }
    
    //Create stocks collection
    Array.prototype.Add=function(ticker,qty,price,lo,hi){this[index++] = new Stock(index,ticker,qty,price,lo,hi);};
    Array.prototype.GetTickers=function(){var n = [];for(var i=0;i<this.length;i++){n[i]=Stocks[i].Ticker;}return n;};
    Array.prototype.GetStockByTickerName=function(tickerName){for(var i=0;i<this.length;i++){if(Stocks[i].Ticker==tickerName && Stocks[i].Updated==0){return Stocks[i];}}};
    Array.prototype.UpdateStocks=function(){for(var i=0;i<this.length;i++){Stocks[i].Updated=0;}fnReqStockData();};
    var Stocks = [];
    var index = 0;

    function fnInit()
    {
        document.getElementById("gVersion").innerHTML='Version:'+iVersion;
        fnReadPrefs();
        fnLoad();
        //setTimeout('fnMessage();_IG_Analytics("UA-2031644-1", "/Portfolio")',2000);
